const uint k = 1103515245U;  // GLIB C
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    return vec3(x) / float(0xffffffffU);
}
#define hash(p)   ihash(uvec3(U.x,U.x*11.,U.x+717.)).x
*/

#define hash(p)  fract(sin( (p) *  78.233 ) * 43758.5453) // old school hash

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    if (U.y > 0.) return;            // we simulates only 1st line 

    if (iFrame==0) {                 // --- initialization
        O = vec4(0);
     // O.x = U.x + U.y*R.x;         // each pixel represent one integer
        O.a = O.x = floor(1e5*hash(U.x));
        return;
    }
                                     // --- Syracuse steps
    O = T(U);                        // previous state
    int i = int(O.x+.5);
    if ( i==1 ) { O.y++; return; }   // already converged ( O.y counts after converged )

    i   = i/2*2==i ? i/2 : 3*i+1;    // hearth of the model
    O.x = float(i);                  // store current value
}

// --- store the whole sequence

void mainImage( out vec4 O, vec2 U )
{
    O = int(U.y)==iFrame  ? texelFetch(iChannel1, ivec2(U.x,0),0)
                          : T(U);
}

// --- draw the AnglePath orbit of the reversed sequence 
//     see see https://community.wolfram.com/groups/-/m/t/558256

float line(vec2 p, vec2 a,vec2 b) {           // --- draw line
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b),
          c = clamp(h, 0., 1.);
    p -= b*h;
    return h==c ? dot(p,p) : 1e5; 
 // return dot(p,p);                          // with round ends
}

void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch( iChannel1, ivec2(U), 0 ); // previous image
    vec2 P, _P;
    if ( iFrame > int(R.y) ) return;          // drawing finished

    float d = 99.,x, a,r, z = R.y/7.;
    U = ( U - vec2(.1,.45)*R ) / z ;

    for ( x=0.; x < min(R.x,640.); x++ ) {    // for each simulated number
        vec4 T = T(vec2(x,iFrame));           // last record
        if ( T.y != 1. ) continue;            // draw curve when sequence just finished
        a = 0.; P = vec2(0); r = .05; // r = 300.*pow(T.a,-1.2);
        for ( int i,t = iFrame; t >= 0; t-- )  { // for each value in the sequence
            _P = P;
            T = T(vec2(x,t)); i = int(T.x+.5);// current value in the reversed sequence
            a += i/2*2==i ? .14 : -.26;       // AnglePath from the sequence:
            P += vec2(cos(a),sin(a)) * r;     // curve turns left it even or right if odd
            d = min(d, line(U,_P,P) );        // draw current hair segment
        }
    }
    O += .2* smoothstep(1.5,0.,sqrt(d) *z ) * vec4(.5,.7,1,1);
}
