#define BLURAMOUNT 2.0
#define THRESHOLD 0.12

float luminance(vec3 col) {
    return 0.2126 * col.r + 0.7152 * col.g + 0.0722 * col.b;
}
float colorDist(vec3 col) {


    return sqrt(col.r * col.r + col.g * col.g + col.b * col.b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 average = vec3(0.0);

    float amount = 0.0;
    for (float y = -BLURAMOUNT; y <= BLURAMOUNT; y += 1.0) {
        for (float x = -BLURAMOUNT; x <= BLURAMOUNT; x += 1.0) {

            average += texture(iChannel0, uv + vec2(x, y) / iResolution.xy).rgb;
            amount += 1.0;

        }
    }

    vec3 texC = texture(iChannel0, uv).rgb;
    vec3 blurC = average / amount;

    vec3 col = vec3(0.0);

    if (distance(texC, blurC) > THRESHOLD) {
        col = texC;
    }

    fragColor = vec4(col,1.0);
}
