void mainImage(out vec4 c, in vec2 f)
{
    vec2 r = iResolution.xy, u = (2. * f - r)/r.y;
    float ss = 10.;
    float time = 1.;

    c = vec4(0.,0.1,.1,1);
    for(int i = 0; i < 20; i++)
    {
        vec2 flu = floor(u * ss);
        vec3 color = (1. - vec3(cos(flu.x/ss + iTime + float(i)), 1. - abs(sin(flu.x/ss + iTime + float(i))), 0)); 
        c.rgb += color.rgb * step(abs(u.y), abs(cos(flu.x/ss + iTime * time) + sin(iTime + flu.x/ss) *0.25));
        time *= .88;
    }
    c /= 20.;

}
