#define PI 3.1415926535897932384626433832795
#define PI2 6.283185307179586476925286766559
#define PI4 12.566370614359172953850573533118


float func(float a) {
    return cos(a);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy/iResolution.xy;

    vec2 cv = vec2(uv.x - .5, uv.y - .5); // uv * .5;
    float dist = 1.0 - distance(cv, vec2(0.0,0.0));
    dist = dist * dist * dist;

    #define COUNT 6
    // int count = 6;
    float phases[COUNT];
    float speeds[COUNT];
    float amps[COUNT];
    float ph = -2.0;
    float sp = .40;
    float amp = .10;
    float phSign = 1.0;
    float spSign = -1.0;
    for (int a=0; a<COUNT; a++ ){
        phases[a] = ph;
        speeds[a] = sp;
        amps[a] = amp;
        amp *= .995;
        ph *= 1.02 * phSign;
        sp *= 1.002 * spSign;
        phSign *= -1.0;
        spSign *= -1.0;
    }
    float z = 0.0;
    float z0 = 0.0;
    float z1 = 0.0;
    float x0 = uv.x;
    float y0 = uv.y;
    for(int a=0; a<COUNT; a++ ) {
        z0 = func(z1 + x0 + uv.x * PI * phases[a] - iTime * speeds[a] +
                func(z0 + y0 + uv.y * PI * phases[a] + iTime * .1) + iTime * amps[a]
                );
        z1 = func(z0 + y0 + uv.x * PI * phases[a] - iTime * .1 +
                func(z1 + x0 + uv.y * PI * phases[a] + iTime * speeds[a]) + iTime * amps[a]
                );
        x0 = z1;
        y0 = z0;
    }
    z0 = z0 * .5 + .5;
    z1 = z1 * .5 + .5;
    z = z0 + z1;
    //z *= dist;

    cv *= 2.0;
    cv = abs(cv);
    float r = z * cv.x;
    float g = z * cv.y;
    float b = (dot(vec2(z0 + cos(iTime), z1 + sin(iTime)), cv.xy) * 1.0);

    fragColor = vec4(r, g, b, 1.0);
}
