// char by FabriceNeyret2
#define char(_p,_C)(((_p.x<0.||_p.x>1.||_p.y<0.||_p.y>1.))?vec4(0,0,0,1e5):textureGrad(iChannel3,_p/16.+fract(vec2(_C,15-_C/16)/16.),dFdx(_p/16.),dFdy(_p/16.)))

void mainImage( out vec4 O, in vec2 U )
{
     #define R iResolution.xy
     #define t 8.*iTime
     vec4 Oo = O;
     //print char sound in
     vec2 u =  3.*(vec2(U.x, U.y)*2.-R)/R.y +vec2(.5, -1.5);
     float a= .92;
     for(int i = 0; i < 7; ++i){
     O += char(u,int[](83, 111, 117, 110, 100, 105, 110)[i]).x* max(0., 1.-t*.04) ;
     Oo.xyz += (cos(floor(sin(t)*u.x)*8.*u.y*vec3(1, 2,3)+2.*iTime));
     u.x -= 1.45;
     u*= mat2(cos(a), -sin(a), sin(a), cos(a));
     }
     //
     vec2 st =  abs(3.*(U*2.-R)/min(R.x,R.y));
     st += .2/length(sin(t+max(st.x,st.y)));
     ivec2 gv = ivec2(st);
     O.xz += vec2(.8, cos(t)*.6+.6)*float(gv.x==(int(t)/2%4)&&gv.y==(int(t)/3%4))/float(iFrame%10);
     O.yz += clamp( sin(st.y +.5*iTime) * ((O.x+O.z)/2.), 0., 1.);
     if(iTime<8.)
     O += mix(min(O,Oo), max(1.-sqrt(Oo), O), sin(t)*.5+.5);
     if(mod(1. + .5 *t, 4.) > 2.)
      O += min(O,Oo);
}

#define _f(x) fract(x)

void hardy(in float rt, in float of, inout float y)
{

    float yy = 0.;

    float tm = (_f(rt *2.) - of);
    float ft = _f(4. *tm);
    float en = (1. + _f(rt));
    float ten = 0.; 
    en = ten = (sqrt(en) *4. *en);

    float sr = .67, pa = .1;

    if (rt > 14.25 && rt < 15.5)
    {
        sr = .4;
        pa = .21;
        en = 1.;
    }

    float xe = pow(max(_f(tm), .0001), pa) * (sin(tm) *sr + sr);

    if(mod(rt, 6.) > 5. && rt > 20.)
        en = (.5 + cos(en));

    if(rt>6.&&rt<8. || rt > 24.&&rt < 30.)
        en = sqrt(ten) *4. *ten;

    yy = (sin(240.5* sin(xe)) *(.5 + cos(en)));

    if(rt > 6. && rt < 12. && mod(1. + .5 *rt, 3.) > 2.)
        yy += .07* sin(1244. *xe);

    yy *= (min(1., 200. *ft) *max(0., 1. - ft));
    y = (clamp(yy, -.5, .5));

    if(rt > 16.1)
        y += .1 * sin(6300. *rt + en) *exp(-10. *_f(8. *rt));

}


#define Fs iSampleRate
vec2 mainSound(in int s, float t)
{
    t = mod(float(s)/Fs,38.);

    vec2 drm0 = vec2(0);

    hardy(t, .503, drm0.x);
    hardy(t, .505, drm0.y);
    return (drm0-.3*(.5*drm0.x-drm0.y));

}
