float grid(vec2 p, vec2 res){
    p = mod(p,res.y);
    float d = length(p-res.y*.5);
    return smoothstep(d,d+1.5/iResolution.y, res.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
    vec2 res = vec2(.25, .5);
    vec2 u = uv;
    u.y += sin(u.x * 7.7) * .05 * uv.y;
    u.y += cos(u.x * 4.2) * .05;
    u.y = abs(u.y - .5);
    u.x = sin(uv.x * 3.) * .5;
    u.x += mod(u.y, res.x);
    u.x -= u.y + iTime * .14;
    u.x += sin(u.y * .1) * .5;
    float mmm = grid(u, res);
    fragColor = vec4(mmm);
}
