    vec2 st = fragCoord/iResolution.xy;

    st = (st*2.0 - 1.0) * 1.0 ;

    vec2 m = iMouse.xy / iResolution.xy;
    m = (m*2.0 - 1.0);

    float y = 0.0;

    y = (((sin( st.y / 100.334 ) + st.x / -2.224) + fract( st.x / -0.282 )) + fract( st.x / st.y )) * m.y;



    vec3 color = vec3(y);



    float pct = plot(st,y);

    color = vec3(pct);

    // Time varying pixel color
