void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;
    float a = (iTime)+6050.;
    float b = 0.5 + 0.36*cos(
                    (p.x*p.x+2.5)/2.
                )-sin(
                    (p.y*p.y+2.5)/2.
                )+2.;

    //*vec3(0,1,1)
    //*vec3(1,0,1)
    fragColor = vec4(0.5*vec3(mod(a,b), mod(a, 1.-b), mod(a,b/2.)),1.0);
}
