#define TAU 6.283185
#define PI TAU * .5
#define SURF_DIST .01

// fractal brownian motion https://thebookofshaders.com/13/
vec3 fbm (vec3 p)
{
    vec3 result = vec3(.0);
    float a = .5;
    for (float i = 0.; i < 3.; ++i) {
        p.y += iTime*.005/a;
        result += sin(texture(iChannel1, p/a).xyz*6.28)*a;
        a /= 2.;
    }
    return result;
}


vec3 irri(float hue) {
  return .5+ .5 *cos(( 9.*hue)+ vec3(0,23.,21.));
}


float melted(vec3 samplePoint,float time, out float aspect) {
    float y = samplePoint.y;

    float angle;
    angle += sin(y + time);
    angle += sin(y * 1.5 + time * 1.5) * 1.25;
    angle += cos(y * 0.03 + time * 1.7) * 1.563;
    //angle += sin(y * 1.1 + time * 1.2) * 1.953;
    angle += cos(y * 1.1 + time * 1.2) * 1.441;
    //angle += sin(y * 1.1 + time * 1.2) * 1.052;

    aspect = angle;
    angle = angle * 3.14159 + 3.14159*2.;

    vec3 offset = vec3(sin(angle), 0.0, cos(angle)) * 0.075;
    return sdSphere(samplePoint + offset);
}



// signed distance function
float map(vec3 p)
{
    float dist = 100.;
    float shape = 100.;
    vec3 pp = p;
    float c;
    float a = 0.;
    // sphere
    dist =melted(p,iTime*0.5,a);

    // droplets
    c = abs(pModPolar(p.xz, 5.));
    vec3 rng = hash31(c);
    p.x -= (1.)-.5;
    rng.x += sign(p.x)*.5;
    p.x = (p.x)-.2;
    p.y *= -1.;
    float time = .2*iTime  +rng.x;
    float anim = fract(time);
    float index = floor(time);
    float wave = sin(anim*3.14);
    float h = .7+.3*pow(wave, 4.);
    float s = .03-.08*(1.-wave);
    shape = sdSegment(p, h, s);
    dist = smin(dist, shape, .2);
    shape = length(p-vec3(0,pow(anim, 8.)*400.+h,0))-.025*(1.-p.y);
    dist = smin(dist, shape, .3*pow(anim,0.5));

    // sphere mouse interaction
    //dist = smin(dist, length(pp+mouse)-.1, .6);

    // surface details
    vec3 seed = pp*.05;
    //vec3 noise = fbm(seed);
    //dist -= noise.x*.01;

    return dist * .5;
}

void coloring (inout vec3 color, in vec3 pos, in vec3 normal, in vec3 ray, in vec2 uv, in float shade)
{
    // Inigo Quilez color palette
    // https://iquilezles.org/www/articles/palettes/palettes.htm
    vec3 tint = .5+.5*cos(vec3(0,.3,.6)*6.283+iTime*.2+uv.y*3.);

    // lighting
    color = vec3(0.15)*pow(dot(normal, vec3(0,-1,0))*.5+.5, 10.);
    vec3 rf = reflect(ray, normal);
    float top = dot(rf, vec3(0,1,0))*.5+.5;
    float glow = dot(normal, ray)*.5+.5;
    color += vec3(.8)*pow(clamp(top,0.,1.), 4.5);
    color += vec3(2)*pow(glow, 2.);
    color *= pow(shade,.5);
    color += irri(top+glow+pos.y*0.1+iTime*0.01)*glow;
    //color += texture(iChannel1,rf).rgb*0.25;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;

    // background
    vec3 color = vec3(0.1);

    // coordinates
    vec3 pos = vec3(0,0,4);
    vec3 at = vec3(0,0,0);
    pos.zy *= rot(cos(iTime*.2)*0.1);
    vec3 ray = lookAt(pos, at, uv, 1.);

    // noise
    //vec3 blue = texture(iChannel0, fragCoord/1024.).xyz;
    //vec3 white = hash33(vec3(fragCoord, iFrame));

    // materialing
    material = 0.;
    glow = 0.;
    float maxDist = 7.;

    // raymarch
    const float count = 50.;
    float steps = 0.;
    float total = 0.;
    for (steps = count; steps > 0.; --steps) {
        float dist = map(pos);
        if (dist < total *1./iResolution.y || total > maxDist) break;
        //dist *= 0.9;
        //ray += white * total*.001;
        pos += ray * dist;
        total += dist;
    }

    // NuSan https://www.shadertoy.com/view/3sBGzV
    vec2 noff = vec2(.001,0);
    vec3 normal = normalize(map(pos)-vec3(map(pos-noff.xyy), map(pos-noff.yxy), map(pos-noff.yyx)));

    // coloring
    float shade = steps/count;
    if (shade > SURF_DIST && total < maxDist) {
        coloring(color, pos, normal, ray, uv, shade);
    }

    fragColor = vec4(color, 1);
}

#define repeat(p,r) (mod(p,r)-r/2.)
mat2 rot(float a) { return mat2(cos(a),-sin(a),sin(a),cos(a)); }
vec3 lookAt (vec3 from, vec3 at, vec2 uv, float fov)
{
  vec3 z = normalize(at-from);
  vec3 x = normalize(cross(z, vec3(0,1,0)));
  vec3 y = normalize(cross(x, z));
  return normalize(z * fov + uv.x * x + uv.y * y);
}
vec3 vectorAt (vec3 from, vec3 at, vec2 uv, float fov)
{
  vec3 z = normalize(at-from);
  vec3 x = normalize(cross(z, vec3(0,1,0)));
  vec3 y = normalize(cross(x, z));
  return (z * fov + uv.x * x + uv.y * y);
}
// Mercury
// https://mercury.sexy/hg_sdf/
float pModPolar(inout vec2 p, float repetitions) {
	float angle = 6.28/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.)) c = abs(c);
	return c;
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions/
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float sdSegment( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}
float sdLink( vec3 p, float le, float r1, float r2 )
{
  vec3 q = vec3( p.x, max(abs(p.y)-le,0.0), p.z );
  return length(vec2(length(q.xy)-r1,q.z)) - r2;
}
float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdSphere( vec3 p )
{
  return length(p) - 1.0;
}

float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }


// blackle
// https://suricrasia.online/demoscene/functions/
vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}
vec3 rndrot(vec3 p, vec4 rnd) {
  return erot(p, normalize(tan(rnd.xyz)), rnd.w*3.1415);
}

// Dave Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}
vec3 hash33(vec3 p3) {
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}
vec4 hash41(float p)
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}
