//https://www.shadertoy.com/view/4djSRW
float hash_WithoutSine(vec2 p)
{
vec3 p3  = fract(vec3(p.xyx) * .1031);
p3 += dot(p3, p3.yzx + 19.19);
return fract((p3.x + p3.y) * p3.z);
}

vec3 colorA = vec3(0.435,0.086,0.953);
vec3 colorB = vec3(0.953,0.369,0.369);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv =  ( fragCoord - .5* iResolution.xy ) / iResolution.y; 

float s = -0.1;
float t = fract(iTime * s * 2.0);

float n = exp(t * 5.0) - 0.9;
vec2 g = 0.5 + uv * n;

//float k = hash_WithoutSine(floor(g)+t*0.001);
float r = 0.4 - t * 0.6;

vec2 offset = vec2(0.5, 0.5);
float u = length(fract(g) - offset);
float c = smoothstep(u, u * 0.97, r);
float p = floor(fract(iTime * s)*2.0);
c = abs(p - c);
vec3 colorC = mix(colorA, colorB, c);
fragColor = vec4(colorC, 1.0);
}
