renderpass.code:
float rand(vec2 p) {
    return fract(sin(dot(p, vec2(12.99, 78.233))) * 43758.545);
}

float noise(vec2 p) {
    vec2 f = fract(p);
    f = f * f * f * f * (3. - 2. * f) * (3. - 2. * f);
    vec2 i = floor(p);
    return mix(mix(rand(i + vec2(0, 0)), 
                   rand(i + vec2(1, 0)), f.x),
               mix(rand(i + vec2(0, 1)), 
                   rand(i + vec2(1, 1)), f.x), f.y);
}

float fbm(vec2 p) {
    float v = 0.;
    float a = 1.;
    for(int i = 0; i < 4; ++i) {
        p = 1.5 * p + 15.;
        a *= 0.5;
        v += a * noise(p);
    }
    return v;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = 2. * fragCoord.xy / iResolution.y;

    vec2 r1 = vec2(fbm(p + 0.02 * iTime), fbm(p + 0.005 * iTime));
    vec2 r2 = vec2(fbm(p + 0.15 * iTime + 10. * r1), fbm(p + 0.12 * iTime + 12. * r1));

    fragColor = vec4(1.8 * pow(fbm(p + r2), 2.) + 0.03);
}
