vec3 sunGradCol1 = vec3(1.0, 0.75, 0.3);
vec3 sunGradCol2 = vec3(1.0, 0.0, 0.6);
vec3 bgCol1 = vec3 (1.0, 0.63, 0.25);
vec3 bgCol2 = vec3 (0.75, 0.0, 0.3);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //sets up the foreground
    float y = uv.y + sin(uv.x * 4.5) * 0.1;
    float t = step(y, 0.2);

    //sets the sun gradient
    vec3 col = mix(sunGradCol1, sunGradCol2, uv.y);
    //vec3(0.2, 0.7, 0.1)

    //makes circle in center
    float aspectRatio = iResolution.x / iResolution.y; //1.77777..

    float radius = .25;

    uv.x *= aspectRatio;

    //controls the x and y of the center of the circle 
    vec2 center = vec2(0.5 * aspectRatio,0.25 + sin(iTime) *.25); 

    vec2 diff = uv - center;

    float dl = length(diff);

    dl = step(radius,dl);


    //sets background gradient
    vec3 bgc = mix(bgCol1, bgCol2, uv.y);

    //mixes bg and sun
    col = mix(col, bgc, dl);

    //mixes bg and foreground
    col = mix(col, vec3(0.5, 0.1, 0.2), t);




    // Output to screen
    fragColor = vec4(col,1.0);
}
