const float pi = 3.14159265359;
const float tau = 2.0*pi;

const float f = 1.0; //changes the amount of spirals
const float k = 100.0; //higher means more bundled up spiral

const vec3 backgroundColor = vec3(.2,.2,.2);
const vec3 spiralColor = vec3(.9,.9,.9);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    float e = 3.0/iResolution.y;

    float radius = k*length(p);
    float angle = atan(p.y, p.x);
    float dist = min(mod(radius - angle, tau/f), mod(angle - radius, tau/f));
    dist /= k;

    vec3 i = texture(iChannel0, fragCoord/iResolution.xy).rgb;
    float intensity = (i.r+i.g+i.b)/3.0;
    float thickness = intensity*pi/k/f;

    float m = smoothstep(e, 0.0, dist - thickness);

    vec3 col = mix(backgroundColor, spiralColor, m);

    fragColor = vec4(col,1.0);
}
