void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p  = -1.0 + 2.0*uv; 

    float pha = 1.0;
    float div = 0.5;
    float scale = 0.5;
    float num = 10.0;
    float xsine = sin(p.x + iTime*pha);
    vec3 col = vec3 (0.0);    

    float wave = round(mod(abs(xsine - p.y), div)*num*(1.0/scale))*(1.0/num);

    col = vec3(wave);


    // Output to screen
    fragColor = vec4(col,1.0);
}
