float note(float freq, float time)
{
    return sin(6.2831*freq*time);
}

float chord(float bass, int intervals[4], float time)
{
    float o = 0.;
    for(int i = 0; i < intervals.length(); i++)
    {
        o += note(intrvls[intervals[i]]*bass, time);
    }
    o /= 4.;
    return o;
}

vec2 mainSound( int samp, float time )
{
    // multiply frequency by sin to get a vibrato
    // int array controls the intervals in semitones of the chord
    float sound = chord(220.*(1.+0.00001*sin(50.*time)), int[4](0,4,7,11), time);

    return vec2( sound*exp(-.7*time) );
}
