///----------------------------------------------------------------------------|
/// XLAT
/// www.cyberforum.ru
///----------------------------------------------------------------------------:

const vec3  GRAY = vec3(0.3, 0.3, 0.3);
const vec3  RED  = vec3(0.5, 0.0, 0.0);

const float W = 0.12; /// The length of the brick.
const float H = 0.06; /// Brick height.
const float S = 0.01; /// Seam thickness.

bool is_seam(vec2 coord)
{
    if(mod(coord.y          , H + S) < S ) return true;
    if(mod(coord.y / (H + S), 2.   ) < 1.) return mod(coord.x       , W + S) < S;
                                           return mod(coord.x + W/2., W + S) < S;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy + vec2(iTime/40., 0.);

    if(is_seam(uv)) fragColor = vec4(GRAY, 1.0);
    else            fragColor = vec4(RED , 1.0);
}
