// extruded variant of https://shadertoy.com/view/fltXRl

void mainImage(out vec4 O, vec2 U){
    float R = iResolution.y;
   // U += vec2(.5,.8)*U.y;
    U = mod( 4.*U, R ) - R/2.;
    O = max( abs(U.yyyy) + ( U.x>0. ? -R/4. : U.x ) , 0. ); // arrows
    if ( U.x<0. ? U.y+U.x > -R/8. : U.y > R/6. && U.y-U.x > -R/4. ) O += .5; // walls
}
