#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 thc(float a, vec2 b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec2 ths(float a, vec2 b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    //uv.x += 0.1 * iTime;
    float a = atan(uv.y, uv.x);
    float r = length(uv);

    float time = 0.2 * iTime;
    float c = 1.;
    float f = 20.;
    vec2 uv2 = 2. * uv * vec2(ths(f * (1. + h21(uv)), c * abs(uv.x) - time), thc(f * (1. + h21(uv)), c * abs(uv.y) - time));
    vec2 uv3 = 2. * uv * vec2(ths(2., c * abs(uv.x) + time), thc(2., c * abs(uv.y) + time));

    if (iFrame % 2 == 0)
        uv = uv2;
    else
        uv = uv3;
        uv = uv2;
    //uv = mix(uv2, uv3, vec2(0.5 + 0.5 * thc(4., 2. * iTime)));
    uv = vec2(ceil(2. * h21(floor(0.5 * iTime) + floor(2. * uv))) * atan(uv.y,uv.x) / pi, log(length(uv))-time);

    //uv += h21(uv) * 0.01 * thc(1000., 4. * r - iTime);
    vec2 v = vec2(cos(a + iTime), sin(a + iTime));
    float sc = 4.;
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float d = length(fpos);
    float k = 0.5;
    a = atan(fpos.y, fpos.x);

    float h = h21(ipos);
    float s = smoothstep(-k, k, 0.5 * (0.5 + 0.5 * thc(2.,(10. + 10. * h) * d + a + 11. * h - iTime )) - d);
    s *= 3. * (0.5-d);
    vec3 col = s* pal(s + cos(h21(ipos) + (1. + 0.005 * h21(uv)) * iTime), vec3(1.), vec3(1.), vec3(1.), 0.1 + 0.4 * vec3(0.,0.33,0.66));
    //col *= 0.8;

    // Output to screen
    fragColor = vec4(col,1.0);
}
