#ifdef GL_ES
precision highp float;
#endif

#define S(a, b, t) smoothstep(a, b, t)
#define sat(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t){
    return sat((t-a) / (b-a));
}

float remap(float a, float b, float c, float d, float t){
    return sat((t-a) / (b-a))*(d-c) +c;
}

vec2 within(vec2 uv, vec4 rect){
    return (uv-rect.xy)/(rect.zw-rect.xy);
}

vec4 Eye(vec2 uv, float side){

    uv.x -=.5;
    //uv.x *= side;
    float d = length(uv)*3.;
    vec4 col = mix(vec4(0.), vec4(0., 0., 0., 1.), S(2., 1.9, d));


    float t = iTime*2.;
    vec2 offs = vec2(sin(t+uv.y*18.), cos(t+uv.x*24.));
    offs *= .1;

    uv-= offs;
    float highlight = S(.07, .06, length(uv-vec2(-.1, .1)))*d;

    highlight = mod(highlight, .5);
    highlight += S(.02, .019, length(uv-vec2(-.01, .01)))*d;
    highlight += S(.05, .049, length(uv-vec2(-.3, .02)))*d;
    highlight += S(.05, .0499, length(uv-vec2(-.2, .4)))*d;
    highlight += S(.006, .00599, length(uv-vec2(-.32, .32)))*d;
    highlight += S(.02, .0199, length(uv-vec2(-.32, -.32)))*d;
    highlight += S(.02, .0199, length(uv-vec2(-.5, -.5)))*d;
    highlight += S(.2, .0199, length(uv-vec2(-.7, 5.)))*d;
    highlight += S(.07, .0699, length(uv-vec2(0., -.5)))*d;
    highlight += S(.07, .0699, length(uv-vec2(0., -1.)))*d;
    highlight += S(.02, .0199, length(uv-vec2(0., .5)))*d;
    highlight += S(.02, .0199, length(uv-vec2(-.5, -.5)))*d;
    highlight += S(.02, .0199, length(uv-vec2(.2, .2)))*d;
    highlight += S(.02, .0199, length(uv-vec2(.2, .1)))*d;
    highlight += S(.02, .0199, length(uv-vec2(.2, .05)))*d;
    highlight += S(.05, .0499, length(uv-vec2(.2, -.05)))*d;
    highlight += S(.08, .0799, length(uv-vec2(.2, -.1)))*d;
    highlight += S(.08, .0799, length(uv-vec2(.05, -.1)))*d;
    highlight += mod(highlight, .7);
    col.bgr = mix(col.rgb, vec3(sin(t*2.), cos(t*2.), .75), highlight);

    //col.a = S(1., 0.9, d);
    return col;
}

vec4 Smiley(vec2 uv){

    vec4 col = vec4(0.);

    float side = sign(uv.x);
    uv.x = abs(uv.x);

    vec4 eye = Eye(uv, side);

    col = mix(col, eye, eye.a);


    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -=.5;
    uv.x *= iResolution.x/iResolution.y;

    fragColor = Smiley(uv);
}
