void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    int i = ( int( texture(iChannel0,U/vec2(6,1)).a > .5  ) + int(iTime/4.) ) % 2;
    O-=O;
    O[i] = texture(iChannel1, U + .1*iTime*vec2(i,0) ).r;
 // O +=       // B&W variant
 // O[1-i] =   // swapped colors variant
}
