void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    float r = .5;
    float maxDis = .00;//(sin(iTime-pi*.5)*.5+.5)*.05;
  //  float maxShift = 5.;
    vec2 shift = vec2(0);
    float dLine = 100000.;

    float t = iTime*speed;
    float stage = floor(t/(maxN*2.));
    float mm = mod(t, maxN*2.);
    float m = floor(mm);
    float dir = floor(m/maxN);
    float fr = dir==0. ? fract(t) : 1.;
    //fr = dir==0. ? fr : 1.-fr;
    float ii = dir==0. ? m : maxN;//maxN*2.-m;
    ii+=1.;//-dir;
    //uv*=Rot(t*pi*.0625*.125);
    float fade = 1.-fract(t);
    bool ending = m==maxN*2.-1.;
    if(ii>0.){
        for(float i=0.;i<ii;i++){
            //vec2 h = N(i*cos(iTime*pi*2.)*.05)*vec2(.05, .001);//hash(vec2(i));
            float step = (i<ii-1. ? 1.: dir==0. ? fr : 1.-fr);
            float dis = maxDis*step;
            vec2 d = vec2(-dis, maxShift);//-dis, sin(t*pi)*maxShift)/ii;
            d*= step;
            float a1 = ii-1.+dir;
            float a2 = ii-dir;
            float a = 0.;//mix(a1==0. ? 0. : (i+1.)*pi*2./(a1), a2==0. ? 0. : (i+1.)*pi*2./a2, fr);
            //a*=.1;
            a+=hash1(vec2((i+1.)*.001)+stage).x*2.*pi;
            mat2 rot = Rot(a);
            mat2 rot1 = Rot(-a);
            d*=rot1;
            vec2 uv1 = uv;
            //uv1 += hash(vec2(i))*i/maxN*(i<ii-1. ? 1.: dir==0. ? fr : 1.-fr);//(2.*(i+.5)*r/ii-r)*.5;//hash1(vec2(i))*.1;
            //uv1+=sin(t*pi*.25)*vec2(N(t*.25))*.05;
            uv1*=rot;

            dis = ending ? fade*dis : dis;
            float newLine = abs(uv1.x)-dis;
            float cut = uv1.y+(step*.25-.25);
            newLine = max(newLine, -cut);
            dLine = min(dLine, newLine);

            float shiftDis = (newLine*cut);
            float rotAng = cut>0. ? shiftDis*sign(uv1.x) : 0.;
            rotAng*= ending ? fade : 1.;
            mat2 cutRot = Rot(rotAng*.5);
            uv*=cutRot;
            vec2 locShift = cut>0. ? (uv1.x<.0 ? -d : d)*shiftDis : vec2(0);
            shift += locShift;
        }
    }
    uv*=1.5;
    float beat = dir==1. ? .8+fract(t/4.)*.5 : 1.+fract(t/4.)*.1;
    beat = ending ? 1.-fract(t/4.) : beat;
    //uv*=beat;

    uv += dir<1. ? shift : shift*((maxN-mod(t, maxN))/maxN);
    vec3 col = vec3(0., 0., fract(length(uv)*5.));
    //float d = circle(uv, r);

    float d = sdBox(uv, vec2(r));
    d = max(d, -dLine);

    vec3 addCol = vec3((abs(shift))*5.,-length(shift)*5.)*10.;

    col+= ending ? addCol*fade : addCol;

    if(dir>0.){
        float id = floor(t);

        vec2 rnd=hash1(vec2(id));
        col.yz*=Rot(-t*pi*.125);
        col+= abs(rnd.x-length(addCol))>.8||abs(rnd.y-addCol.x)<.2 ? 1.*fade : 0.;
    }

    col*=col*smoothstep(5./iResolution.y,.0, d);
    col = pow(abs(col)+.0, vec3(1./2.2 ));
    fragColor = vec4(col,1.0);
}

#define pi 3.1415926535
#define speed 4.
#define maxN 32.
#define maxShift .05

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

float circle(vec2 p, float r){
   return length(p)-r;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

vec2 N(float angle) {
// angle to vector
    return vec2(sin(angle), cos(angle));
}

vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
vec2 hash1( vec2 p )
{
    //p = mod(p, 4.0); // tile
    p = vec2(dot(p,vec2(127.1,311.7)),
             dot(p,vec2(269.5,183.3)));
    return fract(sin(p)*18.5453);
}

vec2 sound(float freq, float dis, float t, float time){
   float x = 0.;
   float mm = mod(t, maxN*2.);
   float m = floor(mm);
   float dir = floor(m/maxN);
   bool ending = m==maxN*2.-1.;
   float fr = ending ? 0. : fract(t);
   float vol = pow(1.-fr, dir==0. ? .001 : .03);
   vol*=clamp(fr*(dir==0. ? 5. : 40.), 0., 1.);
   vol*=clamp((1.-fr)*10., 0., 1.);
   vol*=dir==0. ? .2 : .0;
   vol*=(1.+.05*hash1(vec2(m*.01,dir)).x);
   float id = floor(t);

   float voices = dir==0. ? m : maxN;
   voices+=1.;
   for(float i=1.;i<=voices;i++){
       vec2 hh = hash1(vec2(id, i))*.5+.5;
       float h = hh.x;
       float l = hh.y;
       h=floor(h*7.);
       l=floor(l*4.)-1.;
       h=2./h;
       float w = sin(2.*pi*(freq*h*l)*time);
       x+=sign(w)*pow(abs(w)*vol, i*dis*10.+dir*100.);
       x/=(1.+dir);

   }
   //x=sign(x)*pow(abs(x), 1.01);
   return vec2(x/voices);
}

vec2 mainSound( int samp, float time )
{
   float freq =440.;
   float t=time*speed;
   vec2 x = vec2(0.);
   //x+=sound(freq, 1., t, time)*.5; 

   vec2 dl = sound(freq, .07, t-.25, time);
   dl=sign(dl)*pow(abs(dl), vec2(.5));

   vec2 dr = sound(freq, .05, t-.125, time);
   dr=sign(dr)*pow(abs(dr), vec2(.4));

   x+=dl*vec2(1., .2);
   x+=dr*vec2(.2, 1.);

   //x*=.;

   float mm = mod(t, maxN*2.);
   float m = floor(mm);
   float dir = floor(m/maxN);
   float bb = dir==0. ? 6. : 8.;
   float bb1 = dir==0. ? 6. : 6.;

   float b=sin(2.*pi*freq*time/bb)*(1.-fract(t/4.));
   x+=dir==0. ? 0. : sign(b)*pow(abs(b), dir==0. ? 10. : 7.)*1000000.;

   float b1=sin(2.*pi*freq*time/bb1)*(1.-fract(t/4.));
   //x+=sign(b1)*pow(abs(b1), 50.)*1000.;

   return x/.3;
}
