void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 U =( 2.*fragCoord-iResolution.xy)/iResolution.y;

  float b=sqrt(length(U)),num=128.;

  U=fract(vec2(b,abs(atan(U.x,U.y))));

  num /= 1. + mod(U.x,1.); //"fireworks" effect

  U=floor(mod(vec2(U.x-iTime/4.,U.y)*num,vec2(num)));

  float d=b*mod((U.x*U.y),U.x)/num;

  fragColor=vec4(fract(d*U.x),fract(d*U.y),d,1.);


}

