void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * WORLD_SZ;

    vec3 color = vec3(0);

    float start = hash1in1((iTime*3.0)/float(uint(~0)-1u));

    uint count = 0u;
    vec2 wuv;
    for ( wuv.y = 0.0f; wuv.y < WORLD_SZ; ++wuv.y )
    {
        vec2 h;
        h.y = hash1in1((start + wuv.y) / WORLD_SZ);

        for ( wuv.x = 0.0f; wuv.x < WORLD_SZ; ++wuv.x )
        {
            h.x = (start + wuv.x)/ WORLD_SZ + fract(iTime);//hash1in1(wuv.x / WORLD_SZ);

            float chance = h.y * 2.0f - 1.0f;

            if (chance > 0.9f+start*0.1f) { // is lit?

                vec3 lit_color = unpackColor(mix(h.x,h.y,0.5f) * float(uint(~0)-1u));

                float d = distance(uv, wuv);

                color += lit_color * (1.0f / (d*d+1.0f));
                ++count;
            }
        }
    }

    fragColor = vec4(color,float(count)/float(uint(~0u)-1u));
}

#define WORLD_SZ 48.0

// 128-bit xorshift
//  - Marsaglia, Xorshift RNGs, Journal of Statistical Software, v8n14, 2003
uint xorshift128(uvec4 v)
{
    v.w ^= v.w << 11u;
    v.w ^= v.w >> 8u;
    v = v.wxyz;
    v.x ^= v.y;
    v.x ^= v.y >> 19u;
    return v.x;
}

// xxhash (https://github.com/Cyan4973/xxHash)
//   From https://www.shadertoy.com/view/Xt3cDn
uint xxhash32(uint p)
{
	const uint PRIME32_2 = 2246822519U, PRIME32_3 = 3266489917U;
	const uint PRIME32_4 = 668265263U, PRIME32_5 = 374761393U;
	uint h32 = p + PRIME32_5;
	h32 = PRIME32_4*((h32 << 17) | (h32 >> (32 - 17)));
    h32 = PRIME32_2*(h32^(h32 >> 15));
    h32 = PRIME32_3*(h32^(h32 >> 13));
    return h32^(h32 >> 16);
}

// combined xxhash after xorshift hash functions
float hash1in1(in float one)
{
    uint x = uint(one * float(uint(~0u)-1u));

    x = xxhash32(xorshift128(uvec4(x,0,0,0)));

    return( float(x) / float(uint(~0u)-1u) );
}
float hash2in1(in vec2 two)
{
    uvec2 x = uvec2(two * vec2(uvec2(~0u)-1u));

    x.x = xxhash32(xorshift128(uvec4(x,0,0)));

    return( float(x.x) / float(uint(~0u)-1u) );
}
float hash3in1(in vec3 three)
{
    uvec3 x = uvec3(three * vec3(uvec3(~0u)-1u));

    x.x = xxhash32(xorshift128(uvec4(x,0)));

    return( float(x.x) / float(uint(~0u)-1u) );
}

// these functions are available in GLSL 4.0+
vec4 _unpackUnorm4x8(in uint a)
{
    vec4 c = vec4(0);
    c.a = float((a >> 24u)&0xffu) / 255.0;
    c.b = float((a >> 16u)&0xffu) / 255.0;
    c.g = float((a >> 8u)&0xffu) / 255.0;
    c.r = float((a >> 0u)&0xffu) / 255.0;

    return(c);
}
uint _packUnorm4x8(in vec4 a)
{
    uint c = 0u;
    c |= ((uint(a.a * 255.0)&0xffu) << 24u);
    c |= ((uint(a.b * 255.0)&0xffu) << 16u);
    c |= ((uint(a.g * 255.0)&0xffu) << 8u);
    c |= ((uint(a.r * 255.0)&0xffu) << 0u);

    return(c);
}

// color packing
vec3 unpackColor(in float fetched) {

	return( _unpackUnorm4x8(uint(fetched)).rgb );
}
float packColor(in vec3 pushed) {

	return(float(_packUnorm4x8(vec4(pushed,0.0f))));
}
