float line(vec2 p, vec2 a,vec2 b) {                    // line SDF
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
    return length(p - b * h);                      
}

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, U = u/R,
          P = vec2(1,2), P0;
    float d = 9., l, i,n;

    for( i=0.; i< float(min(1000,iFrame/5)); i++ ) {  // ---polyline loop
        P0 = P, P = hash(P),                          // next point = hash(prev)
        l = line(U, P0,P);
        if (l<d) d=l, n=i;                            // keep closest to pixel
      }

    O = vec4( smoothstep( 1.5/R.y, 0., d ) * (1.-exp2(-2.*n/i)) ); // display
}

  const uint k = 1103515245U;  // GLIB C
//const uint k = 134775813U;   // Delphi and Turbo Pascal
//const uint k = 20170906U;    // Today's date (use three days ago's dateif you want a prime)
//const uint k = 1664525U;     // Numerical Recipes

vec3 uhash3( uvec3 x )         // iq version
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)/float(0xffffffffU);
}

vec3 hash( vec3 f )            // vec3 to vec3. any scaling. f.z=0 seed is ok.
{ 
    return uhash3( uvec3( floatBitsToUint(f.x),
                          floatBitsToUint(f.y),
                          floatBitsToUint(f.z) ) );
}
