float blur = 0.002;

float Rect(vec2 uv, float x, float y, float w, float h) {
    uv.x += w / 2.;
    uv.y += h / 2.;

    float x1 = smoothstep(x - blur, x + blur, uv.x);
    float x2 = smoothstep(x + w + blur, x + w - blur, uv.x);

    float y1 = smoothstep(y - blur, y + blur, uv.y);
    float y2 = smoothstep(y + h + blur, y + h - blur, uv.y);

    return (x1 * x2) * (y1 * y2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    float time = iTime;

    uv -= 0.5;

    float x = uv.x;
    float m = sin(x * 8. + time) * .2;
    m = m*m*5.;
    float y = uv.y - m;

    float mask = Rect(vec2(x ,y), 0., 0., 1., 0.01);
    vec3 col = vec3(1., 1., 0.) * mask;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
