#define ZOOM_ITER 40
#define ZOOM_CENTER vec2(0.5,0.5)
#define ZOOM_AMP 0.02
#define ZOOM_OUTSIDE_AMP 0.15
#define ZOOM_OUTSIDE_OFFSET 0.2
#define CHROMA_MIX 0.2
#define HUGE 9E16
#define PI 3.14159265
#define saturate(i) clamp(i,0.,1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xy;
  float len = length( uv - ZOOM_CENTER );

  vec3 tex = vec3( 0.0 );

  for ( int i = 0; i < ZOOM_ITER; i ++ ) {
    float fi = ( float( i ) + 0.5 ) / float( ZOOM_ITER );
    vec3 blurA = saturate( vec3(
      1.0 - 4.0 * abs( 1.0 / 4.0 - fi ),
      1.0 - 4.0 * abs( 2.0 / 4.0 - fi ),
      1.0 - 4.0 * abs( 3.0 / 4.0 - fi )
    ) ) * 4.0;
    vec3 blurB = vec3(
        1.0 - 2.0 * abs( 1.0 / 2.0 - fi )
    ) * 2.0;
    vec3 blur = mix( blurA, blurB, CHROMA_MIX ) / float( ZOOM_ITER );
    float scaleAmp = ( ZOOM_OUTSIDE_AMP * len + ZOOM_AMP ) * fi + ZOOM_OUTSIDE_OFFSET * len;
    vec2 uvt = ( 1.0 - scaleAmp ) * ( uv - ZOOM_CENTER ) + ZOOM_CENTER;
    tex += blur * texture( iChannel0, uvt ).xyz;
  }

  // fragColor = vec4( tex, 1.0 );
  fragColor = texture(iChannel0, uv);
}

vec2 scaleFromCenter(vec2 coord, float scale) {
  if (scale > 1.0 || scale < 0.0) { return coord; }
  vec2 scaleCenter = vec2(0.5);
  return (coord - scaleCenter) * scale + scaleCenter;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    float scale = 1.0 - mod(iTime * 1.3, 0.8) + 0.4;
    if (scale < 0.0) {
    fragColor = texture(iChannel0, st);
    return;
    }
    vec2 newCoord = scaleFromCenter(st, scale);
    float colorScale = scale * 0.5;
    vec4 resultColor = texture(iChannel0, st) * (1.0 - colorScale + 0.2);
    vec4 newCoordColor = texture(iChannel0, newCoord) * (colorScale - 0.2);
    vec4 result = (resultColor + newCoordColor);
    fragColor = result;
}

vec4 appendFilter(vec4 textureColor) {
    mediump float blueColor = textureColor.b * 63.0;
    mediump vec2 quad1;
    quad1.y = floor(floor(blueColor) / 8.0);
    quad1.x = floor(blueColor) - (quad1.y * 8.0);
    mediump vec2 quad2;
    quad2.y = floor(ceil(blueColor) / 8.0);
    quad2.x = ceil(blueColor) - (quad2.y * 8.0);
    highp vec2 texPos1;
    texPos1.x = (quad1.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.r);
    texPos1.y = (quad1.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.g);
    highp vec2 texPos2;
    texPos2.x = (quad2.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.r);
    texPos2.y = (quad2.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.g);
    lowp vec4 newColor1 = texture(iChannel1, texPos1);
    lowp vec4 newColor2 = texture(iChannel1, texPos2);
    lowp vec4 newColor = mix(newColor1, newColor2, fract(blueColor));

    return mix(textureColor, vec4(newColor.rgb, textureColor.w), 0.7);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = appendFilter(texture(iChannel0, uv));
}
