float ellipse(vec2 ab,float r, vec2 place) 
    {
     float d = length(ab - place);
     float el = smoothstep(r, r - 0.006, d);
      return el;
    }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
  {
    vec2 ab = fragCoord.xy/iResolution.xy;
      ab -= 0.5;
        ab.x *= iResolution.x/iResolution.y;

    float v = ellipse(ab, 0.28, vec2(0.0, -0.2));
    v += ellipse(ab, 0.18, vec2(0.0, 0.2));
    v += ellipse(ab, 0.1, vec2(0.25, 0.03));
    v += ellipse(ab, 0.1, vec2(-0.25, 0.03));
    v += ellipse(ab, 0.12, vec2(0.2, 0.35));
    v += ellipse(ab, 0.12, vec2(-0.2, 0.35));
    v += ellipse(ab, 0.08, vec2(0.25, -0.42));
    v += ellipse(ab, 0.08, vec2(-0.25, -0.42));
    v -= ellipse(ab, 0.05, vec2(0.1, 0.22));
    v -= ellipse(ab, 0.05, vec2(-0.1, 0.22));
    v -= ellipse(ab, 0.19, vec2(0.0, -0.2)); 
    v -= ellipse(ab, 0.07, vec2(0.2, 0.35));
    v -= ellipse(ab, 0.07, vec2(-0.2, 0.35));

    vec3 col = vec3(1.0, 2.0, 1.0)*v;
    fragColor = vec4(1, vec3(v));
     }
