  float K(float x, float y, float r){
    return x*x+y*y-r;
  }
  float E(float x, float y ,float a,float b, float r){
    return (x/a*x/a)+(y/b*y/b)-r;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 p=1.0-2.0*fragCoord.xy/iResolution.xy;
    float x=p.x * iResolution.x/iResolution.y;
    float y=p.y;

      float E1=E(x,y+0.4,0.5,0.3,1.);
      float E2=E(x,y-0.25,0.3,0.5,1.);
      float K1=K(x-0.7,y+0.4,0.1);
      float K2=K(x+0.7,y+0.4,0.1);
      float E3=E(x-0.25,y-0.8,0.4,0.2,0.5);
      float E4=E(x+0.25,y-0.8,0.4,0.2,0.5);
      float E5=E(x+0.25,y-0.2,0.4,0.2,0.5);
      float E6=E(x-0.25,y-0.2,0.4,0.2,0.5);

        float z=E1;
        z=min(z,E2);
        z=min(z,K1);
        z=min(z,K2);
        z=min(z,E3);
        z=min(z,E4);    
        z=min(z,E5);
        z=min(z,E6);
        float f=1.25/z;
        vec3 col=vec3(1.,1.,1.);
          if(f<0.) {
            col.g=f;
            col.b=f;
          }
          else f=0.;
           fragColor = vec4(col, 1.0 );   
}
