void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 prevFrame = texelFetch(iChannel1, ivec2(fragCoord), 0);
    vec2 st = ( fragCoord * iChannelResolution[0].xy) / iResolution.xy;
    vec4 currFrame = texelFetch(iChannel0, ivec2(st), 0);
    fragColor = abs(currFrame - prevFrame);
    //fragColor = currFrame;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = ( fragCoord * iChannelResolution[0].xy) / iResolution.xy;
    fragColor = texelFetch(iChannel0, ivec2(st), 0);
}

vec4 projection(vec2 pos, float scale, float angle, vec2 st)
{
    st = ( st /  iResolution. x) - .5;
    st = st - pos;
    st =  mat2 (cos(angle), -sin(angle), sin(angle), cos(angle)) * st;
    st = st / scale;
    st = ( (st + .5) *  iResolution.x);
    return texelFetch(iChannel1, ivec2(st), 0) * .6;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec4 new = texelFetch(iChannel0, ivec2(fragCoord), 0);  
    if (length(new) < 0.05) {
        new = vec4(0.0);
    }
    vec4 p1 = projection( vec2(0.0, -0.2), .7 - (.26124*sin(iTime)), cos(iTime/5.4) * -1.115351, fragCoord) ;
    vec4 p2 = projection(vec2(sin(iTime)*.2,-0.1), .7 - (.1516*sin(iTime/12.)), sin(iTime/4.0) * 1.12, fragCoord) ;
    vec4 p3 = projection(vec2(sin(iTime)*.1,+0.1), .7 - (.123425*sin(iTime/12.)), sin(iTime/3.09509) * 1.12, fragCoord) ;
    fragColor = new + p1 + p2 + p3 ;
}
