//Logo parameters.
#define A .20
#define B .14

void mainImage(out vec4 color, in vec2 coord)
{
    vec2 res = iResolution.xy;
    vec2 uv = (coord-res/2.)/res.y;
    uv /= 1.+sin(iTime*9.)/(1.+4.*iTime*iTime);

    //Chin
    float dist = A/2.-max(abs(uv.x-A/2.), abs(uv.y+A/2.));
    //Diamond
    dist = max(dist, B-abs(abs(uv.x) + abs(uv.y)-A-B));
    //Cut out
    dist = min(dist, length(max(abs(uv+vec2(-B-A,B))-B,0.)));


    //Distance
    color = vec4(dist*res.y);
}
