float seed = 0.0;

vec3 bgColor = vec3(0, 1, 1);

// random number between 0 and 1
float random() {
    return fract(sin(seed++)*43758.5453123);
}

// a Light is defined by a location and a color
struct Light {
    vec3 location;
    vec3 color;
};

// Sphere is defined by a center and radius and material: color
struct Sphere {
	float radius;
	vec3 center;
    vec3 color;
};

// Ray is define by an origin point and a direction vector
struct Ray {
    vec3 origin;
    vec3 direction;
};


// Intersection code for Ray-Sphere    
float raySphereIntersect(in Ray ray, in Sphere sphere) {

    vec3 rayToSphere = ray.origin - sphere.center;
    float b = dot(rayToSphere, ray.direction);
    float c = dot(rayToSphere, rayToSphere) - (sphere.radius * sphere.radius);
	float disc = b*b - c;
    float t = 1000000.0; // initialize to a large number
    if (disc > 0.0) {
        t = -b - sqrt(disc);
        if (t > 0.00001) {
            return t;
        }
        t = -b + sqrt(disc);
        if (t > 0.00001) {
            return t;
        }  
    }
    return t;
}


Sphere spheres[4];
Light  lights[1];

void makeScene(int f) {
    // CDA Added blue to yellow ball
    spheres[0] = Sphere(1.5, vec3(0, 0, -6), vec3(1, 1, 0.1));   
    // CDA light position and color oscillations
    lights[0] = Light(vec3(-1.0+sin(float(f)/20.0)*3.0,
                           -1.0+cos(float(f)/20.0)*3.0, 
                           -2.0+sin(float(f)/20.0)*3.0), 
                      vec3(abs(tan(float(f)/20.0)+.250)*3.0,     // prefix with abs(trig(..)) 
                           abs(cos(float(f)/20.0)+1.0)*3.0,      // to remove shadow
                           (sin(float(f)/20.0)-1.0)*3.0));       // colour artifacts
 //   lights[0] = Light(vec3(-1.0, 0.0, -4.0), vec3(1, 1, 1));
//    lights[0] = Light(vec3(0.0,0.0, 0.0), vec3(1, 1, 1));
}

int numSamples=1;


/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

   // fragCoord ranges from 
   //   in x: 0.5 to iResolution.x-0.5
   //   in y: 0.5 to iResolution.y-0.5
   // pixel (0,0) is at the bottom left corner

   makeScene(iFrame);

   vec3 rayOrigin = vec3(0.0, 0.0, 0.0);

   float screenDepth = -2.0;
   float screenSize = 1.0;
   if (iResolution.x>640.0) screenSize=2.0;

   float width = iResolution.x/screenSize;
   float height = iResolution.y/screenSize;

   vec3 samp = vec3(0, 0, 0);
   seed = 0.0;
   for (int i=0; i<=1; i++) { // Must compare loop index with const on iphone 
       float x = fragCoord.x + random() - 0.5;
       float y = fragCoord.y + random() - 0.5;


       // map (0.5, w-0.5) to (-1, 1)
       // and (0.5, h-0.5) to (-1, 1)
       x = (x/width)*2.0 - 1.0;
       y = (y/height)*2.0 - 1.0;

       // account for the non-square window
       float aspectRatio = width/height;
       y = y/aspectRatio;

       vec3 rayDirection = normalize(vec3(x, y, screenDepth));

       Ray ray = Ray(rayOrigin, rayDirection);
       Sphere sphere = spheres[0];

       float t = raySphereIntersect(ray, sphere);
       if (t < 1000000.0) {
           vec3 hit = ray.origin + t*ray.direction;
     //      vec3 norm = normalize(hit - sphere.center);
           vec3 hitPointNormal = normalize(hit-sphere.center);
           vec3 hitPointToEye = normalize(vec3(0, 0, 0)-hit);
           vec3 hitPointToLight = normalize(lights[0].location-hit);
           float angle = dot(hitPointNormal, hitPointToLight); // hitPointToEye);
           samp = samp + angle*sphere.color*lights[0].color; // CDA multipled by light color
       } 
   }
   fragColor = vec4(samp/float(numSamples), 1.0); 
}
