float R21(vec2 p) {
    return fract(sin(dot(p.xy, vec2(2.3245,5.234)))*12.5632145);
}

void mainImage(out vec4 o, in vec2 i) {
    vec2 r = iResolution.xy;

    vec2 uv = (i-.5*r)/r.y;
    uv *= 5.;

    vec3 col = vec3(0.);

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = R21(id);
    if (n < .5) gv.x *= -1.;

    float d = abs(abs(gv.x+gv.y)-.5);
    vec2 cUv = gv-sign(gv.x+gv.y+.001)*.5;
    d = length(cUv);

    float width = .1 * length(uv+vec2(sin(iTime),cos(iTime)));

    float mask = smoothstep(.01, -.01, abs(d-.5)-width);

    float angle = atan(cUv.x, cUv.y);
    float checker = mod(id.x+id.y, 2.)*2.-1.;

    float x = checker*angle/1.57+iTime*.2;
    float y = (d-(.5-width))/(2.*width);
    y = abs(y-.5)*2.;

    vec2 tUv = vec2(x,y);
    vec3 irgb = texture(iChannel0, tUv).rgb;
    vec3 neg = vec3(1.,1.,1.) - irgb;

    col += mix(irgb, neg, mask);
    col += mask;

    col *= tUv.y;

    o = vec4(col, 1.);

}
