void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord/8.), 0);
}

vec4 p() {float e=.9-mod(iTime,38.1)/42.1+0.1; return vec4(e/1.9,0.,e,1.);}
vec4 f(vec2 at) {return texelFetch(iChannel0, ivec2(at), 0);}
void mainImage( out vec4 O, in vec2 FC )
{
    float w = iDate.w;
    vec2 a = vec2(1.,0.);
    vec2 b = vec2(0.,1.);
    vec3 R = iResolution/8.;
    vec4 h = f(FC);
    vec4 u = f(FC+b);
    bool bl = FC.y < 1. || (u.g * f(FC-b).g * f(FC+a).g * f(FC-a).g) == 0.;
    bool tp = FC.y + 2.5 > R.y;
    if (h.g > .3) {
        if (tp || u.b > .5) { h.rgb = vec3(.2,.2,.7);}
        if (bl) { h = p(); }
    } else if (h.b > .5 && u.g > .3) {
        h = u;
        if (bl) { h = p(); }
    }
    float v = FC.x;
    if (sin(w*2.134)>0.) { v = R.x - v; }
    float t = float(iFrame) - v;
    t += sin(w*2.4) * 15.;
    t = mod(t, R.x);
    if (tp && t < 3. && t > 0. && sin(w * 24.3) > 0.2) {
        h.r = .1; h.bg = vec2(.4);
    }
    if (mod(iTime, 38.1) < .1) {
        h.rgb = vec3(.2,.2,.7);
    }
    O = h;
}
