void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv.x -= 0.5;

    float loopTime = iTime+0.00001;
    loopTime = mod(loopTime+5.0, 10.0);
    loopTime = abs(loopTime-5.0);
    loopTime -= 1.0;
    float fTime = ceil(loopTime);
    fTime = pow(2.,fTime);


    uv = vec2(fract(uv.x*fTime),
                fract(uv.y*fTime));

    float animTime = mod(loopTime, 1.0);
    animTime = smoothstep(0.0, 1.0, animTime);

    float size = 0.20*(1.0-animTime*0.5);

    float circle1 = step(size, length(uv-0.5+animTime*0.25));
    float circle2 = step(size, length(uv-0.5+animTime*-0.25));
    float circle3 = step(size, length(uv-0.5+animTime*vec2(0.25, -0.25)));
    float circle4 = step(size, length(uv-0.5+animTime*vec2(-0.25, 0.25)));

    float circles = circle1*circle2*circle3*circle4;
    fragColor = vec4(vec3(1.0-circles), 1.0);
    //fragColor += vec4(uv, 0.0, 1.0);
}
