//https://iwasbeingirony.blogspot.com/2010/06/difference-between-rgbm-and-rgbd.html
float MaxRange = 7.0;

vec4 EncodeRGBM(vec3 rgb )
{
    float maxRGB = max(rgb.x,max(rgb.g,rgb.b));
    float M =      maxRGB / MaxRange;
    M =            ceil(M * 255.0) / 255.0;
    return vec4(rgb / (M * MaxRange), M);
}

vec3 DecodeRGBM(vec4 rgbm)
{
    return rgbm.rgb * (rgbm.a * MaxRange);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 c1=texture(iChannel0,uv).rgb;
    //encode rgb
    vec4 rgbm_encode = EncodeRGBM(c1);
    //decode rgbm
    vec4 rgbm_decode = vec4(DecodeRGBM(rgbm_encode), 1.0);

    // Output to screen
    fragColor = mix(rgbm_encode, rgbm_decode, uv.x);
}
