float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    float l = 0.;
    float count = 0.;
    float maxMult = map(sin(iTime * 0.3 ), -1. , 1., 8., 16.);
    float xMult = map(sin(iTime * 0.4 ), -1. , 1., 0.05, maxMult);
    float yMult = map(cos(iTime * 0.2 ), -1. , 1., 0.05, 8.);
    float maxX  = map(sin(iTime * 0.8 ), -1. , 1., 200., 350.);
    float countPlus  = map(sin(iTime * 0.08 ), -1. , 1., 0.08, 0.1);
    for(int i=0;i<10;i++) {
        vec2 pos = uv;
        count += countPlus;
        pos.x += sin(iTime+count + sin(count) * xMult ) * maxX;
        pos.y += cos(iTime+count - cos(count) * yMult ) * 200.;
        vec2 p = (pos * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);
        l += 0.028 * abs(sin(iTime+count*150. * 1.0)) / length(p+l/3.);
    }

	fragColor = vec4(vec3(l), 1.0);
}
