/*
* From ReallySimpleStars by Negs - https://www.shadertoy.com/view/7tBBRc
*/

float PseudoRandom(vec2 xy)
{
	vec2 pos = fract(xy / 128.0f) * 128.0f + vec2(-64.340622f, -72.465622f);
	return fract(dot(pos.xyx * pos.xyy, vec3(20.390625f, 60.703125f, 2.4281209f)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 coord_1 = fragCoord / 10.0; //Changes scale
    coord_1.x += iDate.w * 10.;
    float random_1 = PseudoRandom( floor( coord_1 ) );
    float star_1 = 0.0;
    if( random_1 < 0.02 ) //Ajudsts number of stars
    {
        vec2 star_uv = fract( coord_1 ) * 2.0 - 1.0 ;
        star_1 = ( 1. - abs( star_uv.x * star_uv.y * 7.0 ) ) / 3.;
    }

    vec2 coord_2 = fragCoord / 13.0; //Changes scale
    coord_2.x += iDate.w * 13.;
    coord_2.y += 2.;
    float random_2 = PseudoRandom( floor( coord_2 ) );
    float star_2 = 0.0;
    if( random_2 > 0.98 ) //Ajudsts number of stars
    {
        vec2 star_uv = fract( coord_2 ) * 2.0 - 1.0 ;
        star_2 = ( 1. - abs( star_uv.x * star_uv.y * 7.0 ) ) / 1.5;
    }

    float stars = clamp(star_1, 0.0, 1.0);
    if ( star_2 > 0.0 )
        stars = clamp(star_2, 0.0, 1.0);

    // Output to screen
    fragColor = vec4(stars, stars, stars ,1.0);
}
