const vec3 color2 = vec3(1., 1., 1.);

vec2 hash(vec2 st,float seed){
	st = vec2(dot(st,vec2(127.1,311.7)),
	          dot(st,vec2(269.5,183.3)));
	return -1. + 2. * fract(sin(st) * seed);
}

float noise(vec2 st,float seed){
	vec2 i = floor(st);
	vec2 f = fract(st);

	vec2 u = f*f*(3.-2.*f);

	return mix(mix(dot(hash(i+vec2(0., 0.), seed), f-vec2(0., 0.)),
				   dot(hash(i+vec2(1., 0.), seed), f-vec2(1.,0.)),u.x),
			   mix(dot(hash(i+vec2(0., 1.), seed), f-vec2(0.,1.)),
				   dot(hash(i+vec2(1., 1.), seed), f-vec2(1.,1.)),u.x),u.y);
}

const int octaves = 12;
const mat2 rot = mat2(cos(.75), sin(.75), -sin(.75), cos(.75));
float fbm1(in vec2 _st){
	float result = 0.;
	float a = .95;
	for(int i=0; i<octaves; i++){
		result += a * noise(_st, 43758.5453123);
		_st *= rot * 2.31;
		a *= .4;
	}
	return result + .5014;
}

float pattern(vec2 p, float m, out vec2 q, out vec2 r){
    float t = iTime * 2.;
    q = vec2(fbm1(p + m), fbm1(p + 1. - m));
    r.x = fbm1(p + q + vec2(1.7, 9.2) + .15 * t);
	r.y = fbm1(p + q + vec2(8.3,2.8) + .12 * t);
    return fbm1(p + r.xy);
}

vec4 colour(vec2 c)
{
    vec2 q, r;
    float f = pattern(c, 0.0, q, r) * 1.15;
    vec3 col = color2;
    return vec4((f * f + .252)*col, f);

}

vec3 color (in vec2 fragCoord)
{
    return colour(fragCoord).rgb;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor.rgb = color(uv);
}
