float absin(float t) {
    return 0.5 + sin(t) * 0.5;
}

float ababsin(float t) {
    return 0.75 + sin(t) * 0.25;
}

float abababsin(float t) {
    return 0.7 + sin(t) * 0.24;
}

float absincos(float t, float n){
    return clamp(abababsin(n * 3.14159 * cos(t)), 0. ,1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    //vec4 base = texture(iChannel0, uv); //represents framebuffer feed (alternative)
    vec4 base = vec4(.3, .9, 1., 1.); //represents framebuffer feed
    vec4 game = texture(iChannel1, uv); //represents in-game screen

    float L = 2800. + 50.0 * cos(iTime / 5.);
    float P = 25.;
    float N = 27.0; //should be #defines
    float M = 250.0 + 50.0 * sin(iTime / 5.);
    float x = fragCoord.x;
    float y = fragCoord.y;
    float a = absincos((x - 2.0 * y) / L + iTime * .15, P) * absincos(iTime * .3 + y / M, N);
    //float a = abababsin((x - 2.0 * y) / L + iTime * 5.) * abababsin(N * 3.14159 * cos(iTime + y / M));
    fragColor = base * a + game * (1.-a);
}
