vec3 grad(vec3 color1, vec3 color2, vec3 color3, vec3 color4, vec2 uv){
    return mix(mix(color1, color2, smoothstep(0.0, 1.0, uv.y)),  mix(color3, color4, uv.y), smoothstep(0.0, 1.0,uv.x));
    //return mix(mix(color1, color2,  uv.y),  mix(color3, color4, uv.y),uv.x);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Output to screen
    vec3 col = grad(vec3(1, 0, 0), vec3(0, 1, 0), vec3(0, 0, 1), vec3(1, 1, 0), uv);
    fragColor = vec4(col,1.0);
}
