void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord/iResolution.xy;
    vec2 p1 = vec2(0.25,0.25);
    vec2 p2 = vec2(0.5,0.75);
    vec2 p3 = vec2(0.75,0.25);
    vec2 rp1= p1-p2;
    vec2 rp2= p1-p3;
    vec2 rp3= p1-pos;
    float a1 = atan(rp1.x,rp1.y);
    float a2 = atan(rp2.x,rp2.y);
    float a3 = atan(rp3.x,rp3.y);
    if(a3 > min(a1,a2) && a3 < max(a1,a2)){
        vec2 rp1= p3-p1;
        vec2 rp2= p3-p2;
        vec2 rp3= p3-pos;
        float a1 = atan(rp1.x,rp1.y);
        float a2 = atan(rp2.x,rp2.y);
        float a3 = atan(rp3.x,rp3.y);
        if(a3 > min(a1,a2) && a3 < max(a1,a2)){
            fragColor.r = 1.0;
        }
    } 
}
