void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;
    //uv.x+=sin(uv.y*50.+iTime*mod(uv.x,50.))*sin(iTime*3.)*0.01*mod(uv.x,50.);
    float r= 100.; //150.+sin(iTime)*50.;
    float n=float(int(uv.x*r))+1.;
    float f=0.;
    float b=0.;
    float c=n/r;
    if (mod(n,15.)==0.){
    f=sin((uv.y+iTime)*15.);
    b=cos((uv.y-iTime)*10.);
    c=0.;
    }
    else if (mod(n,3.)==0.){f=1.;c=0.;}
    else if (mod(n,5.)==0.){b=1.;c=0.;};    

    vec3 col = vec3(b,f,c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
