// Texturing
// Eric Galin

const float View = 2800.0;  // View distance
const float Epsilon = 0.01; // Marching epsilon
const int Steps = 400;      // Number of steps

// Lipschitz constant
const float K=1.0;

// Terrain -----------------------------------------------------------------------------------------------
//pour v3
float fbm3(in vec3 p,float l,float a,int o){
    int i;
    float r=0.0;
    for(i=0;i<o;i++){
    r+=a*Noise(p/l);
    a*=0.5;
    l*=0.5;
    }
   return r;
}
//pour v2
float fbm2(in vec2 p,float l,float a,int o){
    int i;
    float r=0.0;
    for(i=0;i<o;i++){
    r+=a*Noise(p/l);
    a*=0.5;
    l*=0.5;
    }
   return r;
}
// Terrain heightfield
// x : Position in the plane
//fonction de l'eau 
float water(in vec2 p)
{
    return 15.*sin(p.x/60.)*cos(p.y/60.0);

}
//fonction de terrain
float Terrain( in vec2 p )
{
	//return 50.0*sin(p.x/100.0)*cos(p.y/100.0);
    return fbm2(p.xy,10.,10.,8)*sin(p.x/100.)*cos(p.y/100.0);
} 

// Implicit surface defining the terrain
// p : Point
//pour le terrain
float ImplicitTerrain(in vec3 p)
{
    //p.z=p.z+10.;
    //fbm(p,350.,200.,5);
    //Union(u,water(vec2(1.0,1.0)));
    //Terrain(p.xy);
    float z = Terrain(p.xy);
    float h = p.z - z; 
    return h;
}
//animation pour bouger l'eau
vec3 animationwater(in vec3 p)
{
    vec3 t=p+vec3(iTime*100.,0,0);
    return t;
}

//pour l'eau
float Implicitwater(in vec3 p)
{
    p=animationwater(p);
    float z = water(p.xy)-20.;
    float h = p.z - z;
    //float t=animationwater(p);
    return h;
}
//object////////////////////////////////////////////
vec3 animationcube(vec3 p)
{
    //allez retour
   // vec3 t=p+vec3(cos(iTime*5.)*50.,0,0);
    //cerculaire
    vec3 t =p+ vec3(sin(iTime*4.)*500., cos(iTime*5.)*500.,0.);
    return t;
}


float Cube( vec3 p, vec3 b )
{
  p=animationcube(p);
  vec3 q = abs(p) - b;

  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}


// Primitive functions -----------------------------------------------------------------------------------

// Sphere 
// p : Point
// c : Center 
// r : Radius
float Sphere(vec3 p, vec3 c,float r)
{
  return length(p-c)-r;
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
float Intersection(float a,float b)
{
    return max(a,b);
}
//pour le trou
float difference(float a, float b)
{
       return max(a,-b);

}
float Union(float a,float b)
{
    return min(a,b);
}
// Scene --------------------------------------------------------------------------

// Potential field of the object
// p : point
float Object(vec3 p)
{
  float u = 
  //pour mon terrain
      ImplicitTerrain(p);
      //pour mon trou
     u=difference(u,Sphere(p,vec3(0.0,0.0,0.0),250.0));
     u=Union(u,Implicitwater(p));
    //u=Union(u,water(vec2(1.0,1.0)));
   u=Union(u,Cube(vec3(p.x+200.,p.y,p.z-120.),vec3(100.,100.,100.)));
  return u;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
    float v = Object(p);
  n.x = Object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = Object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = Object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = Object(p);
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v/K);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Shading and lighting ---------------------------------------------------------------------------

vec3 TextureRock(in vec3 p)
{
  return mix(vec3(0.92,0.91,0.90),vec3(0.74,0.72,0.72),Fbm(p/50.0));   
}

vec3 TextureWater(in vec3 p)
{
  return vec3(0.52,0.75,0.91);   
}

vec3 TextureBorder(in vec3 p)
{
    return vec3(0.25,0.24,0.24);   
}

// Compute color 
// p : Point
// n : Normal
vec3 Color(in vec3 p,in vec3 n)
{
    return TextureRock(p);
}

// Rendering 
// ro, rd : Ray origin and direction
// pip : Picture in picture boolean
vec4 Render( in vec3 ro, in vec3 rd, bool pip )
{
    // Light direction
    vec3 light1 = normalize( vec3(-0.8,-0.3,0.4) );


	vec3 col;
    int it;
    bool b;
    float t = SphereTrace( ro, rd, View,b , it);
	vec3 p = ro + t*rd;
    float vp= Object(p);
    vec3 n = ObjectNormal( p );


    col = Color(p,n);
    // Sky
    if( b==false )
    {
        col = vec3(0.35,0.65,0.95) - rd.z*rd.z*2.5;
    }
	else 
	{
        vec3 ref = reflect( rd, n );
        float fre = clamp( 1.0+dot(rd,n), 0.0, 1.0 );
        vec3 hal = normalize(light1-rd);


         // Pseudo diffuse lighting
		float dif = 0.5*(1.0+dot( light1, n ));
        dif*=dif;

        col = 0.45*col+0.55*dif*vec3(0.35,0.35,0.35);
	}

    // Shading for iterations
    if (pip==true)
    {
    	float s=float(it)/float(Steps);
		return vec4(0.112+0.824*s,0.418+0.556*s,0.611-0.515*s,t);
    }
    else
    {
    	// Gamma with square root
    	return vec4( sqrt(col), t );
    }
}

vec2 RayDirection(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Picture in picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Time as function of the position of the mouse
    float a = 3.14*2.0*1.5*iMouse.x/iResolution.x;

    // Camera transform
    vec3 ro; 
    mat3 cam = Camera( a, ro);   

    // Picture in picture
    bool pip=true;

    // Pixel coordinates
    vec2 p = RayDirection(fragCoord, pip);

    // Camera ray    
    vec3 rd = cam * normalize(vec3(p,5.0));

    // Render
    vec4 res = Render( ro, rd, pip );

    fragColor = vec4( res.xyz, 0.0 );

}

// Texturing
// Eric Galin

// Texturing and noise ---------------------------------------------------------

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in the plane
float Hash(in vec2 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*(p.x+p.y) );
}


// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix( Hash(i+vec2(0,0)), 
                        Hash(i+vec2(1,0)),f.x),
                   mix( Hash(i+vec2(0,1)), 
                        Hash(i+vec2(1,1)),f.x),f.y);
}

// Fractal brownian motion with 4 frequencies
// p : Point
float Fbm(in vec3 p)
{
    return Noise(p)/2.0+Noise(p*2.0)/4.0+Noise(p*4.0)/8.0+Noise(p*8.0)/16.0;
}

// Camera
// a: Rotation angle around z axis
// ro : Ray origin
mat3 Camera(float a, out vec3 ro)
{
	float range=1350.0;
    // Origin
    ro = vec3(range*cos(a),range*sin(a),range/4.0);

    // Target
    vec3 ta = vec3(0.0,0.0,50.0);

	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(0.0, 0.0,1.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
