//Itterations to smooth the noise
float octaves = 8.;
//"Size" of the sampled noise texture | Doubles each itteration
float frequency = 0.125;
//"Height" of the sampled value | Halfes each itteration
float amplitude = 1.;
//Size of the uv space | 1 = noise texture is sampled once across the screen, 2 = twice, etc.
float size = 1.0;
//Final color of the pixel
vec4 final = vec4(0.);
//Radius of the circle in which the noise is shown
float maskRadius = 200.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    maskRadius *= ((sin(iTime)+1.)/2.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy/iResolution.xy)*size;

    for(float i = 0.; i < octaves; i++)
    {
        //Sample region which increases each itteration
        vec4 col = texture(iChannel0, (uv*frequency));

        //Just to get it to be black and white
        col = vec4(col.x *= amplitude);
        //You want to see it, right?
        col.w = 1.0;

        //Added as it is normalized at the end
        final += col;

        //Double/Half the values for the next itteration
        frequency *= 2.;
        amplitude *= .5;

    }

    //Calculate how far away the point is and normalize from 0(outside or edge) to 1(center)
    float mask = (maskRadius - sqrt((fragCoord.x - iResolution.x/2.)*(fragCoord.x - iResolution.x/2.)+(fragCoord.y - iResolution.y/2.)*(fragCoord.y - iResolution.y/2.)))/maskRadius;

    //Normalize to not just have a white image and multiply with mask to get a smooth falloff
    final = (normalize(final)*(octaves/2.))*mask;

    // Output to screen
    fragColor = vec4(final);
}
