float line(float p, float l, float w)
{
	return max(distance(p, l) - w, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 p = fragCoord.xy / iResolution.xy;
	p.x *= iResolution.x / iResolution.y;

	vec3 r = normalize(vec3(p.x - iResolution.x / iResolution.y * 0.5, -1, p.y - 0.5));

	r.x += tan(iTime+sin(p.y))*0.01;
	r.z += sin(iTime*4.01+r.x*2.0)*0.005;

	if (r.z <= 0.0) {
        vec2 sp = mod(r.xy / r.z, 1.0);
		float d = line(sp.x, 0.0, 0.01) *
                  line(sp.y, 0.0, 0.01) *
                  line(sp.x, 1.0, 0.01) *
                  line(sp.y, 1.0, 0.01);

		fragColor.rgb = (1.0-pow(d, 0.06)) * vec3(0.3, 1, 0.2);
	}
}
