void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch( iChannel0, ivec2(U), 0 );
}

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;

    O = textureLod(iChannel1, U,1.);
    float d = length ( O.rgb*255. - vec3(13,163,37) );

    d = (d -100.) / max(.01, fwidth(d));

    // .995 : fade with age     .03 : blur with age
    O =   mix( .995*texture(iChannel0, U,.03), texture(iChannel1, U), smoothstep ( -3., 3., d ) ) // blend on background
        * smoothstep ( 0., 2., abs(d) ); // add black silhouette contouring

}
