void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    float p2 = length(uv);
    float p1 = 1.0 + atan(uv.y,uv.x)/6.28   + p2  *sin(iTime)*5.2 ;
    float fff = fract(p1);

     vec4 r = mix(vec4(.73888,vec3(1.0,.0,.0)),vec4(.0,.0,.6660,1.0),smoothstep(.22,.3,fff));
    // Output to screen
    fragColor = vec4(r);
}
