// by s√∏renpeter (www.darch.dk) 06-06-2021


// https://iquilezles.org/articles/palettes 
// to see this function graphed out go to: https://www.desmos.com/calculator/rz7abjujdj
vec3 cosPalette( float t , vec3 brightness, vec3 contrast, vec3 osc, vec3 phase)
{
    return brightness + contrast*cos( 6.28318*(osc*t+phase) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(0);
    vec2 pos = uv;
    pos *= clamp(sin(iTime*0.3),50.,.5); // change the clamp for zooming

    float pos_t = iTime * 0.1 ;
    pos.x -= sin(pos_t)*0.5;
    pos.y += cos(pos_t)*0.5;
    //pos.x = sin(cos(pos.y + iTime * 0.7) + pos.x + iTime * 0.2);
    //pos.y = cos(pos.y + pos.x + iTime * 0.1);

    float rgb_t = iTime * 0.2 ;
    float angle = atan(pos.y/pos.x);
    float r = sin(angle + rgb_t);
    float g = cos(length(pos)*10. - rgb_t);
    float b = cos(angle + cos(length(pos)*10. + rgb_t));


    vec3 brightness = vec3(0.3);
    vec3 contrast = vec3(1.,0.5,0.1);
    vec3 osc = vec3(0.3,0.5,0.1);
    vec3 phase = vec3(0.5,0.3,0.9);

    float rgbMix = g+(b/r); // line
    rgbMix /= g+(r/b); // vortex
    color = cosPalette(rgbMix, brightness, contrast, osc, phase);

	fragColor = vec4(color, 1.0);
}
