void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float divisor = 9.; // Try different values here!

    int x = int(fragCoord.x / 3. + iTime * 10.);
    int y = int(fragCoord.y / 3. + iTime * 5.);

    float a = mod(float(x ^ y), divisor);

    // Output to screen
    fragColor = vec4(vec3(a),1.0);
}
