  fragColor = vec4(texture(iChannel0, gl_FragCoord.xy / iResolution.xy).rgb, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  int x = int(gl_FragCoord.x);
  int y = int(gl_FragCoord.y);
  ivec2 coord = ivec2(gl_FragCoord.xy);
  float neighbors =
    texelFetch( iChannel0, ivec2(x,     y - 1), 0).r
    +texelFetch(iChannel0, ivec2(x,     y + 1), 0).r
    +texelFetch(iChannel0, ivec2(x - 1, y    ), 0).r
    +texelFetch(iChannel0, ivec2(x + 1, y    ), 0).r
    +texelFetch(iChannel0, ivec2(x - 1, y - 1), 0).r
    +texelFetch(iChannel0, ivec2(x - 1, y + 1), 0).r
    +texelFetch(iChannel0, ivec2(x + 1, y - 1), 0).r
    +texelFetch(iChannel0, ivec2(x + 1, y + 1), 0).r;
  // margin for error
  if (neighbors >= 1.95 && neighbors <= 2.05) {
    fragColor = texelFetch(iChannel0, coord, 0);
  } else if (neighbors >= 2.95 && neighbors <= 3.05) {
    fragColor = vec4(1.,1.,1.,1.);
  } else {
    fragColor = vec4(0.,0.,0.,1.);
  }
  if (iFrame == 0) {
    fragColor = vec4(vec3(round(texture(iChannel1, vec2(coord)/vec2(128.)).r)), 1.0);
  }
}
